
public class Penugasan {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x =  50;
		System.out.println (x);
		x += 3; // x=x +3
		System.out.println(x);
		x -= 3; // x= x-3
		System.out.println(x);
		x *= 3; // x= x*3
		System.out.println(x);
		x /=3; // x= x/3
		System.out.println(x);
		x %=3; // x= x%3
		System.out.println(x);

	}

}
